    &quot;&quot;&quot;
    if re.match(r&apos;^([A-Za-z0-9_\-\.\u4e00-\u9fa5])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,8})$&apos;, email_str):
        return email_str
    else:
