def mobile(mobile_str):
    &quot;&quot;&quot;
    &#x68C0;&#x9A8C;&#x624B;&#x673A;&#x53F7;&#x683C;&#x5F0F;
    :param mobile_str: str &#x88AB;&#x68C0;&#x9A8C;&#x5B57;&#x7B26;&#x4E32;
    :return: mobile_str
    &quot;&quot;&quot;
    if re.match(r&apos;^1[3-9]\d{9}$&apos;, mobile_str):
        return mobile_str
    else:
