def id_number(value):
    &quot;&quot;&quot;&#x68C0;&#x67E5;&#x662F;&#x5426;&#x4E3A;&#x8EAB;&#x4EFD;&#x8BC1;&#x53F7;&quot;&quot;&quot;
    id_number_pattern = r&apos;(^[1-9]\d{5}(18|19|([23]\d))\d{2}((0[1-9])|(10|11|12))(([0-2][1-9])|10|20|30|31)\d{3}[0-9Xx]$)|(^[1-9]\d{5}\d{2}((0[1-9])|(10|11|12))(([0-2][1-9])|10|20|30|31)\d{2}$)&apos;
    if re.match(id_number_pattern, value):
