        parser.add_argument(&apos;mobile&apos;, required=True, location=&apos;json&apos;, type=mobile_type)
        parser.add_argument(&apos;code&apos;, required=True, location=&apos;json&apos;, type=regex(r&apos;^\d{6}$&apos;))
        args = parser.parse_args()
