from flask import g
from functools import wraps


def login_required(f):

    @wraps(f)
    def wrapper(*args, **kwargs):
        # &#x5982;&#x679C;&#x7528;&#x6237;&#x5DF2;&#x767B;&#x5F55;, &#x6B63;&#x5E38;&#x8BBF;&#x95EE;
        if g.userid:
            return f(*args, **kwargs)
        else:
            return {&apos;message&apos;: &apos;Invalid Token&apos;, &apos;data&apos;: None}, 401

    return wrapper


