from flask import request, g, jsonify
from utils.jwt_util import verify_jwt


def get_userinfo():
    &quot;&quot;&quot;&#x83B7;&#x53D6;&#x7528;&#x6237;&#x4FE1;&#x606F;&quot;&quot;&quot;
    # &#x83B7;&#x53D6;&#x8BF7;&#x6C42;&#x5934;&#x4E2D;&#x7684;token
    header = request.headers.get(&apos;Authorization&apos;)

    g.userid = None  # &#x5982;&#x679C;&#x672A;&#x767B;&#x5F55;, userid=None
    if header and header.startswith(&apos;Bearer&apos;):
        # &#x53D6;&#x51FA;token
