        # &#x6821;&#x9A8C;token
        data = verify_jwt(token)
        if data:  # &#x6821;&#x9A8C;&#x6210;&#x529F;
            g.userid = data.get(&apos;userid&apos;)  # &#x5982;&#x679C;&#x5DF2;&#x767B;&#x5F55;, userid=11

