        self.slave = random.choice([&apos;slave1&apos;, &apos;slave2&apos;])

    def get_bind(self, mapper=None, clause=None):
        &quot;&quot;&quot;&#x6BCF;&#x6B21;&#x6570;&#x636E;&#x5E93;&#x64CD;&#x4F5C;(&#x589E;&#x5220;&#x6539;&#x67E5;&#x53CA;&#x4E8B;&#x52A1;&#x64CD;&#x4F5C;)&#x90FD;&#x4F1A;&#x8C03;&#x7528;&#x8BE5;&#x65B9;&#x6CD5;, &#x6765;&#x83B7;&#x53D6;&#x5BF9;&#x5E94;&#x7684;&#x6570;&#x636E;&#x5E93;&#x5F15;&#x64CE;(&#x8BBF;&#x95EE;&#x7684;&#x6570;&#x636E;&#x5E93;)&quot;&quot;&quot;

        state = get_state(self.app)

